def get_setting_value_from_temperatures(temperatures):
    indices = np.searchsorted(-setting_value_to_temperature, -temperatures, side='left')
    valid_indices = (0 <= indices) & (indices < len(setting_value_to_temperature))
    return np.clip(np.where(valid_indices, indices + 1, 127), 0, 127)
